    <div class="signin" style="z-index: 100; position: absolute; width: 1000px; background: #222; padding: 40px; top:50px; border-radius: 4px;box-shadow: 0 15px 35px rgba(0,0,0,0.9);overflow: none;">
            <div class="content" style="position: relative; width: 100%; display: flex; flex-direction: column; align-items: center; gap: 40px; padding-top: 125px; margin: 20px; z-index: 1000;">
                <div class="title" style="margin-top: -150px;">
                    <h3 style="font-size: 34px;">Va salut, astazi facem studiul nr.1</h3>
                    <p style="font-size: 18px;">Puteti incarca/descarca si sterge pozele pe care le incarcati :)</p>
                </div>
                <div class="adaugare">
                    <div class="file-upload">
                        <form id="upload-form" action="upload.php" method="post" enctype="multipart/form-data">
                            <input class="file-upload-input" type="file" name="fileToUpload[]" accept=".jpg, .jpeg, .png" multiple />
                            <button class="file-upload-btn" type="button" id="upload-button">Adauga file mai mic de 25 mb</button>
                            <div class="drag-text">
                                <h3>Trageti file-ul aici</h3>
                            </div>
                        </form>
                    </div>
                    <!-- <div class="progress-container">
                        <div class="progress-bar"></div>
                    </div> -->
                </div>
                <div class="download">
                <?php
$dir = 'uploaded_images/';
$images = scandir($dir);
$displayedImages = array();

foreach ($images as $image) {
    if (!in_array($image, array(".", ".."))) {
        $displayedImages[] = $image;
    }
}

$displayedImages = array_reverse($displayedImages);

foreach ($displayedImages as $image) {
    $displayName = (strlen($image) > 16) ? substr($image, -16) : $image;

    // Check if the download counter file exists
    $counterFile = 'download_counters/' . pathinfo($image, PATHINFO_FILENAME) . '.txt';
    if (!file_exists($counterFile)) {
        file_put_contents($counterFile, '0');
    }

    // Read the current download count
    $downloadCount = intval(file_get_contents($counterFile));

    echo <<<HTML
<div class="flex">
    <div class="img_info">
        <img class="uploaded_image" src="$dir$image" alt="$image" />
        <p class="image_name">$displayName</p>
    </div>
    <div class="links" style="display: flex; justify-content: center; align-items: center; width: 50px;">
        <div class="delete" style="margin-right: 10px;">
            <a class="delete_link" href="delete.php?image=$image"><span class="material-symbols-outlined"> delete</span></a>
        </div>
        <div class="download">
            <a class="download_link" style="color: #0f0;" href="$dir$image" download="$displayName"><span class="material-symbols-outlined">download</span></a>
            <span class="download_counter">($downloadCount)</span>
        </div>
    </div>
</div>
HTML;
}
?>


                </div>
            </div>
    </div> 

    

